<?php
// Background image
$featured_bg_url = get_the_post_thumbnail_url(get_the_ID(), 'full');
if (!$featured_bg_url) {
    $featured_bg_url = get_template_directory_uri() . '/img/hero-slide-1.webp';
}

// ACF fields (all optional)
$page_title = get_field('page_title');
$page_subtitle = get_field('page_subtitle');
$title_top_pct = get_field('page_title_position'); // number
$btn_left_text = get_field('button_left_text');
$btn_left_link = get_field('button_left_link');
$btn_left_color = get_field('button_left_color');   // btn-green | btn-black | btn-white
$btn_right_text = get_field('button_right_text');
$btn_right_link = get_field('button_right_link');
$btn_right_color = get_field('button_right_color');  // btn-green | btn-black | btn-white
$show_usps = get_field('usps');                // true/false
$additional_text = get_field('additional_text');     // WYSIWYG
$banner_form = get_field('banner_form');         // contact_us | servicing | empty

// Flags
$has_left_btn = !empty($btn_left_text) && !empty($btn_left_link);
$has_right_btn = !empty($btn_right_text) && !empty($btn_right_link);
$has_any_btn = $has_left_btn || $has_right_btn;
$has_form = !empty($banner_form);

// Safe color classes
$valid_colors = ['btn-green', 'btn-black', 'btn-white'];
$left_color_class = in_array($btn_left_color, $valid_colors, true) ? $btn_left_color : 'btn-black';
$right_color_class = in_array($btn_right_color, $valid_colors, true) ? $btn_right_color : 'btn-green';

// Section classes
$section_classes = 'page-banner';
if ($has_form) {
    $section_classes .= ' has-form';
}
$post_slug = get_post_field( 'post_name', get_post() );
?>
<section class="<?php echo esc_attr($section_classes); ?> <?php echo esc_attr( $post_slug ); ?>">
    <?php if (is_single() && get_field('brand_header_image')) : ?>
        <img src="<?php echo esc_url(get_field('brand_header_image')); ?>" alt="<?php the_title_attribute(); ?>">
    <?php else : ?>
        <img src="<?php echo esc_url($featured_bg_url); ?>" alt="<?php the_title_attribute(); ?>">
    <?php endif; ?>

    <div class="page-banner__inner<?php echo $has_form ? ' page-banner__inner--split' : ''; ?>">

        <!-- Left column: copy -->
        <div class="content"<?php if (!empty($title_top_pct)) : ?> style="top:<?php echo esc_attr((float)$title_top_pct); ?>%"<?php endif; ?>>
            <div class="lpb-banner-content aos-init aos-animate" data-aos="fade-down">
                <h2 class="page-title">
                    <?php echo !empty($page_title) ? esc_html($page_title) : get_the_title(); ?>
                </h2>

                <?php if (!empty($page_subtitle)) : ?>
                    <h1 class="page-subtitle"><?php echo ($page_subtitle); ?></h1>
                <?php endif; ?>

                <?php if (!empty($additional_text)) : ?>
                    <div class="lpb-additional_text">
                        <?php echo wp_kses_post($additional_text); ?>
                    </div>
                <?php endif; ?>

                <?php if ($has_any_btn) : ?>
                    <div class="lpb-cta-group">
                        <?php if ($has_left_btn) : ?>
                            <a class="lpb-btn <?php echo esc_attr($left_color_class); ?>"
                               href="<?php echo esc_url($btn_left_link); ?>">
                                <?php echo esc_html($btn_left_text); ?>
                            </a>
                        <?php endif; ?>
                        <?php if ($has_right_btn) : ?>
                            <a class="lpb-btn <?php echo esc_attr($right_color_class); ?>"
                               href="<?php echo esc_url($btn_right_link); ?>">
                                <?php echo esc_html($btn_right_text); ?>
                            </a>
                        <?php endif; ?>
                    </div>
                <?php endif; ?>

